import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link, Route, useHistory } from "react-router-dom"
import { render, renderHook } from '@testing-library/react';
import subscription from '../addsub/AddSub'

const Profile = () => {
    const [user, setUser] = useState(0)

    const [subscriptions, setSubs] = useState([])

    const historyTwo = useHistory();

    const signOutSubmitHandler = () => {
        console.log('Sign out clicked');
        localStorage.clear();
        historyTwo.push('/');
    }

    useEffect(() => {
        const params = {
            email: localStorage.getItem('loggedInUser')
        }
        let userEmail = localStorage.getItem('loggedInUser');
        axios.get(`http://localhost:8080/findAllSubsByEmail?userEmail=${userEmail}`).then(response => {
            console.log(response);
            setSubs(response.data);
        }).catch(error => {
            console.log("error" + error)
        });
    }, [])

    const deleteHandler = () => {
       
        const params = {

            email:localStorage.getItem('loggedInUser'),
            id: localStorage.getItem('addedSubscription')
        }
        let subscriptionId = localStorage.getItem('addedSubscription')
        axios.get(`http://localhost:8080/deleteSub?id=${subscriptionId}`).then((response) => {
            console.log(response)
            localStorage.removeItem("subscriptions", response.data.subscriptions)


        }).catch((error) => {
            console.log("error" + error)
        })
    }






    return (
        <div className="container-fluid home-margin-top-less-200px">
            <h1 className="h1">Welcome{user.firstName}!</h1>
            <div className="row">
            </div>
            <div>
                <br></br>
                <main role="main" class="inner cover">

                    <h2 className="cover-heading">Active Subscriptions</h2>
                    <table class="table sortable">
                        <thead>
                            <tr>
                                <th scope="col">Name</th>
                                <th scope="col">Plan</th>
                                <th scope="col">Cost $</th>
                                <th scope="col">Payment Date</th>
                                <th scope="col">Cancel Link</th>
                                <th scope="col">Remove Sub</th>
                            </tr>
                        </thead>

                        {subscriptions.map(sub => {
                            console.log(sub)
                            return <tbody>
                                <tr>
                                    <td>{sub.name}</td>
                                    <td>{sub.plan}</td>
                                    <td>{sub.cost}</td>
                                    <td>{sub.paymentDate}</td>
                                    <td><a href={sub.cancelLink}>{sub.cancelLink}</a></td>
                                    <td><button onClick={deleteHandler} className="btn btn-outline-success" type="button">Delete Sub</button> </td>
                                </tr>
                            </tbody>

                        })}

                    </table>
                </main>
            </div>
            <a href="add-sub" class="btn btn-lg btn-secondary fw-bold border-white bg white">Add Subscription</a>
            <button className="btn btn-outline-success" onClick={signOutSubmitHandler} type="button">Logout</button>
        </div>

    )
}


export default Profile;
