import React, { useState } from 'react';
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./style.css";


export default function SignUp() {
    const history = useNavigate();

    const [user, setUser] = useState({
        firstName:"",
        lastName:'',
        email:"",
        password:"",
        age:"",
    })



const userChangeHandler = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    const tempUser = {...user};
    tempUser[name] = value;
    setUser(tempUser)

}

const signUpSubmitHandler = () => {
    axios.post("http://localhost:8080/save", user).then((response) => {
        localStorage.setItem("loggedInUser", response.data.tempUser)
        history.push("/thank-you");
}).catch((error) => {
console.log("User object not placed" + console)
    })
}
const SignUpForm = ({
    history,
    onSubmit,
    onChange,
    errors,
    user,
    score,
    btnTxt,
    type,
    pwMask,
    onPwChange
  }) => {
return (
    <form className="row g-3">
    <h2> Sign up below!!!</h2>
    <div className="col-md-6">
        <label for="inputFirstName" className="form-label">First Name</label>
        <input name="firstName" type="text" className="form-control" id="inputFirstName" />
    </div>
    <div className="col-md-6">
        <label for="inputLastName" className="form-label">Last Name</label>
        <input name="lastName" type="text" className="form-control" id="inputLastName" />
    </div>

    <div className="col-md-6">
        <label for="inputEmail4" className="form-label">Email</label>
        <input name="email" type="email" className="form-control" id="inputEmail4" />
    </div>
    <div className="col-md-6">
        <label for="inputAge" className="form-label">Age</label>
        <input name="age" type="age" className="form-control" id="inputAge" />
    </div>
    <div className="col-md-6">
        <label for="inputPassword4" className="form-label">Password</label>
        <input name="password" type="password" className="form-control" id="inputPassword4" />
    </div>

 
</form>
)
  }
}
