import React, { useState } from 'react';
import axios from "axios";
import { useHistory } from "react-router-dom";

export default function AddSub() {
    const history = useHistory();

    const [Subscription, setSubscription] = useState({
        name: "",
        plan: '',
        cost: "",
        paymentDate: "",
        cancelLink: "",
    })



    const subscriptionChangeHandler = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        const tempUser = { ...subscription };
        tempSubscription[name] = value;
        setSubscription(tempSubscription)

    }

    const addSubSubmitHandler = () => {
        axios.post("http://localhost:8080/savesub", user).then((response) => {
            localStorage.setItem("addedSubscription", response.data.tempSubscription)
            history.push("/profile");
        }).catch((error) => {
            console.log("Subscription object not placed" + error)
        })
    }

    return (
        <form className="row g-3">
            <center><h2> Add a New Subscription</h2></center>
            <div className="col-md-6">
                <label for="inputName" className="form-label">Name</label>
                <input name="name" value={subscription.name} onChange={userChangeHandler} type="text" className="form-control" id="inputName" />
            </div>
            <div className="col-md-6">
                <label for="inputPlan" className="form-label">Plan</label>
                <input name="plan" value={subscription.plan} onChange={userChangeHandler} type="text" className="form-control" id="inputPlan" />
            </div>

            <div className="col-md-6">
                <label for="inputCost" className="form-label">Cost</label>
                <input name="cost" value={subscription.cost} onChange={userChangeHandler} type="text" className="form-control" id="inputCost" />
            </div>
            <div className="col-md-6">
                <label for="inputPaymentDate" className="form-label">Payment Date</label>
                <input name="paymentDate" value={subscription.paymentDate} onChange={userChangeHandler} type="text" className="form-control" id="inputPaymentDate" />
            </div>
            <div className="col-md-6">
                <label for="inputCancelLink" className="form-label">Cancel Link</label>
                <input name="cancelLink" value={subscription.cancelLink} onChange={userChangeHandler} type="text" className="form-control" id="inputCancelLink" />
            </div>
            <div className="d-grid gap-2">
                
                <button onClick={addSubSubmitHandler} className="bg-light btn btn-outline-success" type="button">Add Subscription</button>
            </div>

        </form>
    )
}
